import React, { useState, useEffect } from 'react';
import axios from 'axios';

const BookingForm = () => {
  const [doctors, setDoctors] = useState([]);
  const [doctorId, setDoctorId] = useState('');
  const [userName, setUserName] = useState('');
  const [date, setDate] = useState('');
  const [time, setTime] = useState('');

  useEffect(() => {
    const fetchDoctors = async () => {
      try {
        const response = await axios.get('/api/doctors'); // Fetch doctors from the API
        setDoctors(response.data); // Set doctors in state
      } catch (error) {
        console.error('Error fetching doctors:', error);
      }
    };

    fetchDoctors(); // Call the fetch function
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const appointmentData = { doctor: doctorId, user: userName, date, time };

    try {
      await axios.post('/api/bookings', appointmentData);
      alert('Appointment booked successfully!');
    } catch (error) {
      console.error(error);
      alert('Error booking appointment.');
    }
  };

  return (
    <form onSubmit={handleSubmit} className="booking-form">
      <h2>Book an Appointment</h2>
      <select 
        value={doctorId} 
        onChange={(e) => setDoctorId(e.target.value)} 
        required
      >
        <option value="">Select a Doctor</option>
        {doctors.map(doctor => (
          <option key={doctor._id} value={doctor._id}>
            {doctor.name} - {doctor.specialization}
          </option>
        ))}
      </select>
      <input 
        type="text" 
        placeholder="Your Name" 
        value={userName} 
        onChange={(e) => setUserName(e.target.value)} 
        required 
      />
      <input 
        type="date" 
        value={date} 
        onChange={(e) => setDate(e.target.value)} 
        required 
      />
      <input 
        type="time" 
        value={time} 
        onChange={(e) => setTime(e.target.value)} 
        required 
      />
      <button type="submit">Book Appointment</button>
    </form>
  );
};

export default BookingForm;
