import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom'; // Import useHistory for navigation

const DoctorList = () => {
  const [doctors, setDoctors] = useState([]);
  const history = useHistory(); // Use useHistory hook for navigation

  useEffect(() => {
    const fetchDoctors = async () => {
      const response = await axios.get('/api/doctors'); // Adjust URL if needed
      setDoctors(response.data);
    };

    fetchDoctors();
  }, []);

  const handleBookDoctor = (doctorId) => {
    // Navigate to the booking page with the doctor ID
    history.push(`/book/${doctorId}`); // Assuming you have a route set up for booking
  };

  return (
    <div>
      <h2>Available Doctors</h2>
      <ul>
        {doctors.map(doctor => (
          <li key={doctor._id}>
            <h3>{doctor.name}</h3>
            <p>Specialization: {doctor.specialization}</p>
            <p>Location: {doctor.location}</p>
            <p>Availability: {doctor.availability}</p>
            <button onClick={() => handleBookDoctor(doctor._id)}>Book Doctor</button> {/* Button for booking */}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default DoctorList;
